// Extract ingredients and dietary preferences from webhook payload
const payload = $input.first().json;
const ingredients = payload.ingredients || [];
const dietaryPreferences = payload.dietaryPreferences || [];
const userId = payload.userId;

// Validate input
if (!ingredients || ingredients.length === 0) {
  throw new Error('No ingredients provided');
}

// Return data for processing
return {
  json: {
    ingredients: ingredients,
    dietaryPreferences: dietaryPreferences,
    userId: userId
  }
};

// Generate recipe prompt for GPT-3.5-turbo
const data = $input.first().json;
const ingredients = data.ingredients.map(ing => `${ing.name} (${ing.quantity})`).join(', ');
const dietaryPrefs = data.dietaryPreferences.length > 0 ? 
  `Dietary preferences: ${data.dietaryPreferences.join(', ')}. ` : '';

const prompt = `Create a delicious recipe using: ${ingredients}. ${dietaryPrefs}

Provide in this JSON format:
{
  "name": "Recipe Name",
  "ingredients": [
    {"name": "Ingredient Name", "quantity": "Amount"}
  ],
  "instructions": [
    "Step 1",
    "Step 2"
  ],
  "dietaryPreference": ["vegan", "gluten-free"],
  "additionalInformation": {
    "tips": "Cooking tips",
    "variations": "Recipe variations",
    "servingSuggestions": "How to serve",
    "nutritionalInformation": "Nutritional info"
  }
}

Make it creative and follow dietary preferences if specified.`;

return {
  json: {
    prompt: prompt,
    userId: data.userId
  }
};

// Parse the recipe from GPT-3.5 response
const gptResponse = $input.first().json;
const recipeText = gptResponse.choices[0].message.content;

// Try to extract JSON from the response
let recipe;
try {
  // Find JSON in the response (it might be wrapped in markdown)
  const jsonMatch = recipeText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    recipe = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found in response');
  }
} catch (error) {
  // If parsing fails, create a basic recipe structure
  recipe = {
    name: "Generated Recipe",
    ingredients: [],
    instructions: [recipeText],
    dietaryPreference: [],
    additionalInformation: {
      tips: "",
      variations: "",
      servingSuggestions: "",
      nutritionalInformation: ""
    }
  };
}

return {
  json: {
    recipe: recipe,
    userId: $('Generate Recipe Prompt').first().json.userId
  }
};

// Add placeholder image and complete the recipe response
const data = $input.first().json;
const recipe = data.recipe;
const userId = data.userId;

// Use a placeholder image from Unsplash (free)
const placeholderImage = `https://source.unsplash.com/1024x1024/?${encodeURIComponent(recipe.name + ' food')}`;

// Create the complete response
const completeRecipe = {
  ...recipe,
  imgLink: placeholderImage,
  userId: userId,
  openaiPromptId: `generated-${Date.now()}`
};

return {
  json: {
    recipe: completeRecipe,
    success: true
  }
};
